export const initialState = {
  basket: [],
};

// selector
export const getBasketTotal = (basket) => basket?.reduce((amaount, item))
const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "Book":
      return {
        ...state,
        basket: [...state.basket, action.item],
      };
    default:
      return state;
  }
};

export default reducer;
